				local function randowImage()
					w1.isVisible = false
					w2.isVisible = false
					w3.isVisible = false
					w4.isVisible = false
					w5.isVisible = false
					w6.isVisible = false
					local theNumber = math.floor(math.random(5))
					if theNumber == 0 then
						w1.isVisible = true
					elseif theNumber == 1 then
						w2.isVisible = true
					elseif theNumber == 2 then
						w3.isVisible = true
					elseif theNumber == 3 then
						w4.isVisible = true
					elseif theNumber == 4 then
						w5.isVisible = true
					elseif theNumber == 5 then
						w6.isVisible = true
					end
				end
				
				local timeRandomized = 0
				local changed = false
				local prevTime = system.getTimer()
				
				local lastFrame = 0
				animatePhase = function()
					local actualFrame = math.floor(PivotAnimation.getActualFrame())
					if actualFrame > lastFrame then
						lastFrame = actualFrame
						if lastFrame == 1 then
							playFlipSound()
						end
						if lastFrame == 74 then
							playFlopSound()
						end
						if lastFrame == 124 then
							playFlapSound()
						end
					end
					if PivotAnimation.getActualFrame() >= 143 and not changed then
						changed = true
						randowImage()
						MaryAnimation.hide()
					end
					
					if changed then
						timeRandomized = timeRandomized + system.getTimer() - prevTime
						if timeRandomized > 50 then
							randowImage()
							timeRandomized = 0
						end
					end
					
					local maryfinished = (math.floor(PivotAnimation.getFrameCount()) == math.floor(PivotAnimation.getActualFrame())) and (PivotAnimation.getTimesLooped() == maxLoops-1)
					
					if maryfinished then
						animatePhase = nil
						pausePhase=nil
						restartPhase=nil
						phaseTwo.kill()
						vanish()
					end
				end
				
				pausePhase = function()
					PivotAnimation.stop()
					MaryAnimation.stop()
					maryBlinks.stopBlinking()
				end
				
				restartPhase = function()
					PivotAnimation.start()
					MaryAnimation.start()
					maryBlinks.startBlinking()
				end
			end
			
			phaseTwo.kill = function()
				if MaryAnimation then
					maryBlinks.stopBlinking()
					MaryAnimation.kill()
					MaryAnimation=nil
				end
			end
		end
		phaseTwo.prepare()
		
		local pausedState = paused
		
		local function animate()
			if pausedState ~= paused then
				pausedState = paused
				if paused then
					BlueAnimation.stop()
					BlackAnimation.stop()
					blackBlinks.stopBlinking()
					blueBlinks.stopBlinking()
					if pausePhase then
						pausePhase()
					end
				else
					BlueAnimation.start()
					BlackAnimation.start()
					blackBlinks.startBlinking()
					blueBlinks.startBlinking()
					if restartPhase then
						restartPhase()
					end
				end
				return
			end
			if paused then
				Runtime:removeEventListener( "enterFrame", animate )
				return
			else
				if animatePhase then
					animatePhase()
				end
			end
		end
		
		local function continue ()
			startMaryWalkingAnimation ()
		end
		
		local function kill()
			localGroup:remove(background)
			
			blackBlinks.stopBlinking()
			blueBlinks.stopBlinking()
			
			BlackAnimation.kill()
			BlueAnimation.kill()
			
			kill = nil
			killMaryGreetingAnimation = nil
		end
		killMaryGreetingAnimation = function()
			pauseIt=nil
			continueIt=nil
			vanish = nil
			kill()
		end
		
		vanish = function()
			pauseIt=nil
			continueIt=nil
			timer.performWithDelay(500,continue)
			timer.performWithDelay(1500,kill)
		end
		
		startMaryGreetingAnimation = function ()
			soundController.playNew{
