		local vanishing = false
		
		local animate
		
		local function continue()
			Runtime:removeEventListener( "enterFrame", animate )
			localGroup:remove(background)
			
			maryBlinks.stopBlinking()
			maryAnimation.kill()
			
			blackBlinks.stopBlinking()
			blackAnimation.kill()
			
			blueBlinks.stopBlinking()
			blueAnimation.kill()
		end
		
		local function vanish()
			soundController.kill("wind")
			
			pauseIt=nil
			continueIt=nil
			
			vanishing = true
			
			startTransition ()
			timer.performWithDelay(1000, continue)
		end
		
		local pausedState = paused
		local timeLimit = 5000
		local timeAnimated = 0
		local prevTime = 0
		animate = function()
			if maryAnimation.displayObject then
				if pausedState ~= paused then
					pausedState = paused
					if paused then
						local prevTime = system.getTimer()
						maryAnimation.stop()
						maryBlinks.stopBlinking()
						blackAnimation.stop()
						blackBlinks.stopBlinking()
						blueAnimation.stop()
						blueBlinks.stopBlinking()
					else
						local prevTime = system.getTimer()
						maryAnimation.start()
						maryBlinks.startBlinking()
						blackAnimation.start()
						blackBlinks.startBlinking()
						blueAnimation.start()
						blueBlinks.startBlinking()
					end
				end
				if pausedState then
					Runtime:removeEventListener("enterFrame",animate)
					return
				end
				
				local curTime = system.getTimer()
				local dt = curTime - prevTime
				if dt > 66 then
					dt = 1
				end
				timeAnimated = timeAnimated + dt
				local fps = 1000/dt
				prevTime = curTime;
				
				if timeAnimated > timeLimit and not vanishing  then
					vanish()
				end
				
				background.x = background.x - 2 * (timeAnimated/1000) * (timeAnimated/1000) * 60/fps
				
				maryAnimation.displayObject.x = maryAnimation.displayObject.x + (math.sin(timeAnimated/500))/2
				blackAnimation.displayObject.x = blackAnimation.displayObject.x + (math.sin(timeAnimated/1000))*3/4
				blueAnimation.displayObject.x = blueAnimation.displayObject.x - (math.sin(timeAnimated/200))/3
				
				while background.x <= -(background.contentWidth/2) do
					background.x = background.contentWidth/2+background.x
				end
				while background.x >= 0 do
					background.x = -background.contentWidth/2+background.x
				end
			end
		end
		
		startMaryWalkingAnimation = function()
			soundController.playNew{
						path = "assets/sound/effects/cap4/wind.mp3",
						identifier = "wind",
						staticChannel = 2,
						loops = -1,
						actionTimes = nil,
						action =	nil,
						onComplete = nil
						}
			
			local prevTime = system.getTimer()
			
			maryAnimation.start()
			maryAnimation.appear(300)
			
			maryBlinks.openEyes()
			maryBlinks.startBlinking()
			
			blackAnimation.start()
			blackAnimation.appear(300)
			
			blackBlinks.openEyes()
			blackBlinks.startBlinking()
			
			blueAnimation.start()
			blueAnimation.appear(300)
			
			blueBlinks.openEyes()
			blueBlinks.startBlinking()
			
			background.isVisible = true
			background.alpha = 0
			transition.to(background,{alpha=1,time=300})
			
			Runtime:addEventListener( "enterFrame", animate )
			
			pauseIt = function()
			end
			
			continueIt = function()
				Runtime:addEventListener( "enterFrame", animate )
			end
		end
	end
