	local killAdventure
	local killDemoLoop
	local killInteraction
	local killTheParty
	local killTransition
	
	local gameOver
	local pauseIt
	local continueIt
	
	local function killAll()
		paused = nil
		gameOver = nil
		pauseIt = nil
		continueIt = nil
		screenMessages.kill()
		if killAdventure then
			killAdventure()
		end
		if killDemoLoop then
			killDemoLoop()
		end
		if killInteraction then
			killInteraction()
		end
		if killTheParty then
			killTheParty()
		end
		if killTransition then
			killTransition()
		end
	end
	
