			loadingBackground.isVisible = true
			titleLabel.isVisible = true
			
			pauseIt = function()
				--timer.pause(thisTimer)
			end
			
			continueIt = function()
				--timer.resume(thisTimer)
			end
			
			startAdventure = nil
		end
