				local changed = false
				local prevTime = system.getTimer()
				
				local lastFrame = 0
				animatePhase = function()
					local actualFrame = math.floor(PivotAnimation.getActualFrame())
					if actualFrame > lastFrame then
						lastFrame = actualFrame
						if lastFrame == 1 then
							playFlipSound()
						end
						if lastFrame == 74 then
							playFlopSound()
						end
						if lastFrame == 124 then
							playFlapSound()
						end
					end
					if PivotAnimation.getActualFrame() >= 143 and not changed then
						changed = true
						randowImage()
						MaryAnimation.hide()
					end
					if changed then
						timeRandomized = timeRandomized + system.getTimer() - prevTime
						if timeRandomized > 50 then
							randowImage()
							timeRandomized = 0
						end
					end
					local prevTime = system.getTimer()
					
					local maryfinished = (math.floor(PivotAnimation.getFrameCount()) == math.floor(PivotAnimation.getActualFrame())) and (PivotAnimation.getTimesLooped() == maxLoops-1)
					
					if maryfinished then
						Runtime:removeEventListener("enterFrame",animatePhase)
						animatePhase = nil
						if phaseTwoFinished then
							if type(phaseTwoFinished) == "function" then
								phaseTwoFinished()
							end
						end
					end
				end
				Runtime:addEventListener("enterFrame",animatePhase)
				
				pausePhase = function()
					PivotAnimation.stop()
					maryBlinks.stopBlinking()
				end
				
				restartPhase = function()
					PivotAnimation.start()
					maryBlinks.startBlinking()
				end
			end
			
			phaseTwo.kill = function()
				maryBlinks.stopBlinking()
				MaryAnimation.kill()
			end
